    { title = "–ì–∞–≤–Ω–æ–¥–∞–≤–∏", definition = "–ë—Ä—É–¥–Ω–µ, –Ω–µ–≥–∞—Ä–Ω–µ, –∑–Ω–æ—à–µ–Ω–µ –≤–∑—É—Ç—Ç—è, –∞–±–æ —â–µ —Ç—è–∂–∫–µ –≤–∑—É—Ç—Ç—è ‚Äì —á–µ—Ä–µ–≤–∏–∫–∏, —á–æ–±–æ—Ç–∏ –ü—Ä–∏–∫–ª–∞–¥: ‚Äú–ú–æ–¥–Ω—è—á—ñ –≥–∞–≤–Ω–æ–¥–∞–≤–∏‚Äù ‚Äú–®–∫–æ–¥–∞ –≤—ñ–¥–¥–∞–≤–∞—Ç–∏ –∫—É–ø—É –≥—Ä–æ—à–µ–π –∑–∞ –≤–∑—É—Ç—Ç—è, —â–æ–± –ø–æ—Ç—ñ–º –ø–µ—Ä–µ—Ç–≤–æ—Ä–∏—Ç–∏ —ó—Ö –≤ –≥–∞–≤–Ω–æ–¥–∞–≤–∏‚Äù ‚Äú–•–æ—á–µ—à –ø—ñ—Ç–∏ –ø–æ–≥—Ä–∞—Ç–∏ –∑ –¥—Ä—É–∑—è–º–∏ ‚Äì –±–µ—Ä–∏ –≥–∞–≤–Ω–æ–¥–∞–≤–∏, –∞ —Ö–æ—á–µ—à –ø–æ–∫–ª–µ—ó—Ç–∏ —Ç—å–æ–ª–æ—á–æ–∫ ‚Äì –ø—Ä–∏–π–¥–µ—Ç—å—Å—è –∫—É–ø—É–≤–∞—Ç–∏ –Ω–æ—Ä–º–∞–ª—å–Ω–µ –≤–∑—É—Ç—Ç—è‚Äù –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ì–∞–≤–Ω–æ–º–µ—Ç–∏ —à–∫–∞—Ä–∏ —á—É–≤—è–∫–∏ –ì—ñ–≤–Ω–æ–¥–∞–≤–∏ –ì—ñ–≤–Ω–æ" },
    { title = "–ì–∞–≤–Ω–æ–º–µ—Ç–∏", definition = "–ñ—É—Ä–Ω–∞—à–ª—é—à–∫–∏, –æ—Å–Ω–æ–≤–Ω–∏–º –¥–æ—Ö–æ–¥–æ–º —è–∫–∏—Ö —î –Ω–∞–ø–∏—Å–∞–Ω–Ω—è –ø—ñ–¥–ª–∏—Ö –∑–∞–∫–∞–∑–Ω–∏—Ö –Ω–µ–≥–∞—Ç–∏–≤–Ω–∏—Ö —Å—Ç–∞—Ç–µ–π, –≤ –æ—Å–Ω–æ–≤–Ω–æ–º—É, –Ω–∞ –ø–æ—Ä—è–¥–Ω–∏—Ö –ª—é–¥–µ–π. –ü—Ä–∏–∫–ª–∞–¥: –í –£–∫—Ä–∞—ó–Ω—É –Ω–µ –ø—É—Å—Ç–∏–ª–∏ ‚Äú–ª—é–±–æ–≥–æ –¥—Ä—É–≥–∞‚Äù –≥–∞–≤–Ω–æ–º–µ—Ç–∞ –®–∞—Ä—ñ—è. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ñ—É—Ä–Ω–∞—à–ª—é—à–∫–∞, –∂—É—Ä–Ω–∞–ª—ñ–∑–¥ –º–µ–¥—ñ–∞—Ç—ñ—Ç—É—à–∫–∏ –ó–ª–∏–≤–Ω—ñ –±–∞—á–∫–∏ –ü—ñ—Ö–æ—à–∫–∏ –î–∂–∏–Ω—Å–∞" },
    { title = "–î–∞ –ø–∞—à–ª—ñ –≤–∏", definition = "–ù–∞—Ä–∞–∑—ñ –≥–æ–ª–æ–≤–Ω–µ –¥–æ—Å—è–≥–Ω–µ–Ω–Ω—è –ø–∞–Ω–∞ –ê–∑–∞—Ä–æ–≤–∞ —É –ø–ª–∞–Ω—ñ —Ç–≤–æ—Ä–µ–Ω–Ω—è –∫—Ä–∏–ª–∞—Ç–∏—Ö –≤–∏—Ä–∞–∑—ñ–≤ –ü—Ä–∏–∫–ª–∞–¥: –ú–∏–∫–æ–ª–∞ –ê–∑–∞—Ä–æ–≤: –¢–æ–º—É —è –ø—Ä—ñ–∑–∏–≤–∞—é –≤–∞—Å: –¥–∞ –ø–∞—à–ª—ñ –≤–∏! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ê–∑—ñ—Ä—ñ–≤–∫–∞ –ë—ñ–º–±–∞ —Å–≤—è—Ç–æ –ü–∞–∫—Ä–∞—â—ñ –ü–∞–ø—î—Ä—î–¥–Ω—ñ–∫—ñ –í–æ—Ä –ê–∑–∞—Ä–æ–≤" },
    { title = "–¥–∞–≤–∞—Ç–∏ –Ω–∞ –ª–∞–ø—É", definition = "–µ–≤—Ñ–µ–º—ñ–∑–º –¥–æ –≤–∏—Ä–∞–∑—É ‚Äú–¥–∞–≤–∞—Ç–∏ —Ö–∞–±–∞—Ä‚Äù –ü—Ä–∏–∫–ª–∞–¥: ‚Äú–≤ –Ω–∞—Å –ª—é–¥–∏ –∑–≤–∏–∫–ª–∏ –¥–∞–≤–∞—Ç–∏ —á–∏–Ω–æ–≤–Ω–∏–∫–∞–º –Ω–∞ –ª–∞–ø—É, –∞ –ø–æ—Ç—ñ–º —â–µ –¥—è–∫—É–≤–∞—Ç–∏, —â–æ –≤–∑—è–ª–∏‚Äù, ‚Äì —Å–∫–∞–∑–∞–≤ –°–∞–∞–∫–∞—à–≤—ñ–ª—ñ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –Ω–∞–¥–∞–≤–∞—Ç–∏ –ø–æ –≥—É–±–∞—Ö –†—ñ—à–∞–ª–∞ –¥–∞–≤–∞—Ç–∏ –ø—Ä–æ—Å—Ä–∞—Ç–∏—Å—è –¥–∞–≤–∏—Ç–∏ –≥—É—Å–∞–∫–∞ –ü–æ–Ω—è—Ç—ñ–π–∫–∞" },
    { title = "–î–∞–≤–∞—Ç–∏ –ø—Ä–æ–ø–∏–∑–¥–æ–Ω—ñ–≤", definition = "–î–∞–≤–∞—Ç–∏ –ø—Ä–æ—á—É—Ö–∞–Ω–∞; –≤—Å—Ç–∞–≤–ª—è—Ç–∏/–≤–∏–ø–∏—Å—É–≤–∞—Ç–∏ –ø—Ä–æ–ø–∏–∑–¥–æ–Ω—ñ–≤ –ü—Ä–∏–∫–ª–∞–¥: –ü—Ä–∏–π—à–æ–≤ –ø–æ–∑–∞–≤—á–æ—Ä–∞ –¥–æ–¥–æ–º—É —É—à–∞—Ç–∞–Ω–∏–π –≤ –∫—ñ–∑–ª–æ, —Ç–æ –º–∞–º–∫–∞ –¥–∞–≤–∞–ª–∞ –ø—Ä–æ–ø–∏–∑–¥–æ–Ω—ñ–≤ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –í—ñ–¥–¥—É—Ö–æ–ø–µ–ª–∏—Ç–∏ –á–±–æ—à–∏—Ç–∏ –õ—é–ª—ñ –•—É—è—Ä–∏—Ç–∏ –ü–∏–∑–¥–æ—Ä—ñ–∑" },
    { title = "–¥–∞–≤–∞—Ç–∏ –ø—Ä–æ—Å—Ä–∞—Ç–∏—Å—è", definition = "1) –¥–∞–≤–∞—Ç–∏ –ø—Ä–æ—á—É—Ö–∞–Ω–∞, —Å—Ç–∞–≤–∏—Ç–∏ –Ω–∞ –º—ñ—Å—Ü–µ 2) –ø—Ä–æ—á–∏—â–∞—Ç–∏ (–ø—Ä–æ –¥—ñ–≤–∞–π—Å–∏) 3) –Ω–∞–¥–∞—Ç–∏ –∫–æ–º—É—Å—å –º–æ–∂–ª–∏–≤—ñ—Å—Ç—å –ø—Ä–æ—Å—Ä–∞—Ç–∏—Å—è (–ø–æ–∫–∞–∫–∞—Ç–∏ —É –≤—Å—ñ–π –ø–æ–≤–Ω–æ—Ç—ñ –∑–Ω–∞—á–µ–Ω–Ω—è —Ü—å–æ–≥–æ —Å–ª–æ–≤–∞) –ü—Ä–∏–∫–ª–∞–¥: –í–∞—Å—è –¥–∞–≤ –ø—Ä–æ—Å—Ä–∞—Ç–∏—Å—è –ü—î—Ç—ñ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —Å—Ä–∞–∫–∞-–º–æ—Ç–∏–∫–∞, –≥—ñ–º–Ω–æ-–∫–∞–º—ñ–∑–µ–ª—å–∫–∞ –°—Ä–∞–∫–∞ —Å—Ä–∞–∫–∞-–±–∞–Ω—å–∫–∞ –°—Ä–∞—á–∫–∞ –°—Ä–∞–∫–∞—á" },
    { title = "–¥–∞–≤–∏—Ç–∏ –≥—É—Å–∞–∫–∞", definition = "–º–∞—Å—Ç—É—Ä–±—É–≤–∞—Ç–∏, –¥—Ä–æ—á–∏—Ç–∏ (–ø—Ä–æ —á–æ–ª–æ–≤—ñ–∫—ñ–≤) –ü—Ä–∏–∫–ª–∞–¥: ‚Äì –∞ –¥–µ –Ü–≤–∞–Ω? ‚Äì —è —ó–±—É. –≥—É—Å–∞–∫–∞ –ø—ñ—à–æ–≤ –¥–∞–≤–∏—Ç–∏ –º–∞–±—É—Ç—å –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ì–∞–Ω—è—Ç–∏ –ª–∏—Å–æ–≥–æ –ö–æ–∂–µ–Ω –¥—Ä–æ—á–µ —è–∫ —Ö–æ—á–µ –ú–∞—Å—Ç—É—Ä–±–∞—Ç–∞—Ç–æ–Ω –í–∏–¥—Ä–æ—á–∫–∞ –î—Ä–æ—á–∏–ª–æ" },
    { title = "–î–∞–≤–∏—Ç–∏ –≥—É—Å—è", definition = "–ú–∞—Å—Ç—É—Ä–±—É–≤–∞—Ç–∏ (—á–æ–ª–æ–≤—ñ–∫) –ü—Ä–∏–∫–ª–∞–¥: –Ø –∑–∞–±–æ—Ä–æ–Ω—è—é —Ç–æ–±—ñ –¥–∞–≤–∏—Ç–∏ –≥—É—Å—è ! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –≥—Ä–∞ ‚Äú–ø‚Äô—è—Ç–µ—Ä–æ –Ω–∞ –æ–¥–Ω–æ–≥–æ‚Äù —Å–Ω—ñ–≥—É—Ä—É–≤–∞—Ç–∏ –¥–∞–≤–∏—Ç–∏ –≥—É—Å–∞–∫–∞ –ì–∞–Ω—è—Ç–∏ –ª–∏—Å–æ–≥–æ –ö–æ–∂–µ–Ω –¥—Ä–æ—á–µ —è–∫ —Ö–æ—á–µ" },
    { title = "–î–∞–π –ø‚Äô—è—Ç—å!", definition = "–ñ–µ—Å—Ç, —â–æ —ñ–Ω–æ–¥—ñ —Å—É–ø—Ä–æ–≤–æ–¥–∂—É—î—Ç—å—Å—è –∞–Ω–∞–ª–æ–≥—ñ—á–Ω–∏–º –º–æ–≤–Ω–∏–º –ø—Ä–∏–≤—ñ—Ç–∞–Ω–Ω—è–º. –ú–æ–∂–µ –±—É—Ç–∏ –ø–æ–≤—ñ—Ç—Ä—è–Ω–∏–º, –∫–æ–ª–∏ —Ç–æ–π, —Ö—Ç–æ –≤—ñ—Ç–∞—î—Ç—å—Å—è, –ø—ñ–¥–Ω—ñ–º–∞—î —Ä—É–∫—É –≤–≥–æ—Ä—É –¥–æ–ª–æ–Ω–µ—é –¥–æ —Ç–æ–≥–æ, –∑ –∫–∏–º –≤—ñ—Ç–∞—î—Ç—å—Å—è. –ß–∞—Å—Ç—ñ—à–µ –≤–æ–Ω–∏ –æ–¥–Ω–æ—á–∞—Å–Ω–æ –ø—ñ–¥–Ω—ñ–º–∞—é—Ç—å —Ä—É–∫–∏, –∑‚Äô—î–¥–Ω—É—é—á–∏ –∑ –ø–ª–µ—Å–∫–æ–º –¥–æ–ª–æ–Ω—ñ. –ü—Ä–∏–∫–ª–∞–¥: –î–∞–π –ø‚Äô—è—Ç—å- –¥–∞–≤–Ω–æ –Ω–µ –±–∞—á–∏–ª–∏—Å—è! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ê –±–æ–¥–∞–π —Ç–µ–±–µ –µ–±–æ–ª–∞ –≤—Ö–æ–ø–∏–ª–∞! –∫—É-–∫—É –¥–∞–π–∫–∞ –ü‚Äô—è—Ç–∫–∞ –•—É–π–¥–∞–π" },
    { title = "–µ–≤–∞–∫", definition = "1. –µ–≤–∞–∫—É–∞—Ü—ñ—è (–ø–æ—Ä–∞–Ω–µ–Ω–∏—Ö) 2. –µ–≤–∞–∫—É–∞—Ü—ñ–π–Ω–∏–π –∞–≤—Ç–æ–º–æ–±—ñ–ª—å/–µ–≤–∞–∫—É–∞—Ç–æ—Ä –ü—Ä–∏–∫–ª–∞–¥: 1. –ø—ñ—Å–ª—è —Ç–æ–≥–æ –µ–≤–∞–∫—É –ú–æ—Ç—Ä—è –Ω–µ –º–æ–≥–ª–∞ –Ω–æ—Ä–º–∞–ª—å–Ω–æ —Å–ø–∞—Ç–∏ 2. –ì–∞–≤—Ä–∏–ª–æ –∑–±–∏—Ä–∞–≤ –≥—Ä–æ—à—ñ –Ω–∞ —Ä–µ–º–æ–Ω—Ç –µ–≤–∞–∫ –±—É—Å—ñ–∫–∞ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –±–µ—Ö–∞ –ö–æ–º–±–∞—Ç–∞–Ω—Ç –¢—Ä—å–æ—Ö—Å–æ—Ç–∏–π –î–≤–æ—Ö—Å–æ—Ç–∏–π —à—É—à–ø–∞–Ω—Ü–µ—Ä" },
    { title = "–ï–≥–æ–º–∞–Ω—å—è–∫", definition = "–õ—é–¥–∏–Ω–∞, –≤ —è–∫–æ—ó –µ–≥–æ —Ä–æ–∑—Ä–æ—Å–ª–æ—Å—è —Ç–∞–∫, —â–æ –¥—É—à–∏—Ç—å —É—Å—ñ —ñ–Ω—à—ñ –ø—Ä–æ—è–≤–∏ –æ—Å–æ–±–∏—Å—Ç–æ—Å—Ç—ñ –ü—Ä–∏–∫–ª–∞–¥: –ê–ª–∫–æ–≥–æ–ª—ñ–∫ –¢–∞—Ä–∞—Å –±—É–≤ —Ç–æ–π —â–µ –µ–≥–æ–º–∞–Ω—å—è–∫, –ª—é–¥–µ–π –¥–æ–≤–∫–æ–ª–∞ —Å–µ–±–µ –≤—ñ–Ω –Ω–µ –ø–æ–º—ñ—á–∞–≤ –≤ –ø—Ä—è–º–æ–º—É —Å–µ–Ω—Å—ñ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –π–æ–≥–∞—ñ–∑–º —Å—Ö–æ–ø–∏—Ç–∏ –±–æ–≥–∞ –∑–∞ –ø—ñ—Å—é–Ω—á–∏–∫ –í–∫–ª—é—á–∞—Ç–∏ –∑—ñ—Ä–∫—É –ø–æ—Ö—É—ó–∑–º –ñ–ª–æ–±" },
