    { title = "–ñ–∞—Ö–∞–π–∫–∞", definition = "1)–§—ñ–ª—å–º–∏ –∂–∞—Ö—ñ–≤, –ø—Å–∏—Ö–æ–ª–æ–≥—ñ—á–Ω—ñ —Ç—Ä–∏–ª–µ—Ä–∏ (–∫–æ—Ä–æ—á–µ —Ç—ñ –∫—ñ–Ω–æ–≤–∏–¥–æ–≤–∏—Å—å–∫–∞, —á–µ—Ä–µ–∑ —è–∫—ñ —Ç–∏ –≤—Å—Ü–∏–∫–∞—î—à—Å—è –Ω–æ—á–∞–º–∏ –±–µ–∑ —É–≤—ñ–º–∫–Ω–µ–Ω–æ–≥–æ —Å–≤—ñ—Ç–ª–∞ —Å–ø–∞—Ç–∏ —á–∏ –≤ —Ç—É–∞–ª–µ—Ç –∑–±—ñ–≥–∞—Ç–∏) –ü—Ä–∏–∫–ª–∞–¥: ‚Äì –Ω–∞–≤—ñ—Ç—å –Ω–µ —Å–º—ñ–π —à–æ—Å—å –ø–æ–≥–∞–Ω–µ –ø–∞—Ç—è–∫–∞—Ç–∏ –ø—Ä–æ ‚Äú–ö–æ—à–º–∞—Ä –Ω–∞ –≤—É–ª–∏—Ü—ñ –í‚Äô—è–∑—ñ–≤‚Äù! –¢–æ –∂ –∫—É–ª—å—Ç–æ–≤–∞ –∂–∞—Ö–∞–π–∫–∞! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ñ–∞—Ö–ª–∏–≤—á–∏–∫ –ú–æ–Ω—Å—Ç—Ä—É–æ–∑–Ω–∏–π –ø–æ—á–≤–∞—Ä–∞ –°—Ç—Ä—å–æ–º–Ω–∏–π –£–∂–æ—Å" },
    { title = "–∑ –≥—ñ–≤–Ω–∞ —ñ –ø–∞–ª–æ–∫", definition = "–ø—Ä–æ —â–æ—Å—å –Ω–µ–¥–æ–ª—É–≥–µ, –∑–ª—ñ–ø–ª–µ–Ω–µ –∑ –ø—ñ–¥—Ä—É—á–Ω–∏—Ö, –Ω–∞—è–≤–Ω–∏—Ö –º–∞—Ç–µ—Ä—ñ–∞–ª—ñ–≤ –ü—Ä–∏–∫–ª–∞–¥: –ö–∞—Ä–ø–æ –ø–æ–±—É–¥—É–≤–∞–≤ —Å–∞—Ä–∞–π –∑ –≥—ñ–≤–Ω–∞ —ñ –ø–∞–ª–æ–∫ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –¢–∏ —à–æ, –≥—ñ–≤–Ω–∞ –≤‚Äô—ó–±–∞–≤? –≥–∞–º–Ω—è–Ω–∏–π —É–º–∞—Ç–æ–≤–∏–π –•—Ä—î–Ω—å –≥–∞–ª—ñ–º–∏–π" },
    { title = "–∑ –∫–∞–Ω–¥–∏–±–æ–±–µ—Ä–æ–º", definition = "–∑ –≤–∏–∫—Ä—É—Ç–∞—Å–∏–∫–æ–º, –∑ –≤–∏—ó–±–æ–Ω—á–∏–∫–æ–º, –∑ –≤–∏–±—Ä–∏–∫–æ–º –ü—Ä–∏–∫–ª–∞–¥: –Æ–ª—è –±—É–ª–∞ –¥–∞–º–∞ –∑ –∫–∞–Ω–¥–∏–±–æ–±–µ—Ä–æ–º ‚Äì –≤—Å–µ –∑–∞–≤–∂–¥–∏ —Ä–æ–±–∏–ª–∞ –ø–æ-—Å–≤–æ—î–º—É –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –≤–∏–π–æ–±—É–≤–∞—Ç–∏—Å—è –ø–æ–Ω—Ç –¥—É—Ä–µ–Ω—å —Ö—É–π –ø—Ä–æ—Å—Ü–∏—à –•–∏—Ç—Ä–æ–≤–∏—ó–±–∞–Ω–∏–π" },
    { title = "–ó –æ–¥–Ω—ñ—î—ó –ø—ñ—Å–æ—á–Ω–∏—Ü—ñ", definition = "–ó–Ω–∞–π–æ–º—ñ –∑ —Ä–∞–Ω–Ω—å–æ–≥–æ –¥–∏—Ç–∏–Ω—Å—Ç–≤–∞. –ü—Ä–∏–∫–ª–∞–¥: –ú–∏ –∑ –Ω–∏–º –∑ –æ–¥–Ω—ñ—î—ó –ø—ñ—Å–æ—á–Ω–∏—Ü—ñ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ø–∞—Å–æ—á–∫–∏ –∫–æ—Ä—ñ—à –ë—Ä–∞—Ç—ñ—à –±–æ–π—Ñ—Ä–µ–Ω–¥ –¢—Ä–∞–Ω–∑–∏—Ç–Ω–∏–π –¥—Ä—É–≥" },
    { title = "–ó —è–∫–æ–≥–æ –±—É—è?", definition = "–ù–∞ –∂–∞—Ä–≥–æ–Ω—ñ –º–æ—Ä—è–∫—ñ–≤: ‚Äù –ß–æ–≥–æ –± —Ü–µ?‚Äù –ü—Ä–∏–∫–ª–∞–¥: –ó —è–∫–æ–≥–æ –±—É—è –≤ —Ä–æ—Å—ñ–π—Å—å–∫–æ–º—É –∑–∞–∫–æ–Ω–æ–ø—Ä–æ–µ–∫—Ç—ñ –ø–æ —Ä—ñ—à–µ–Ω–Ω—é —Å—É–¥—É –º–æ–∂—É—Ç—å –∑—Ä–æ–±–∏—Ç–∏ –ø—Ä–∏–º—É—Å–æ–≤–∏–π –∞–±–æ—Ä—Ç –≤ –ª—é–±–∏–π —Ç–µ—Ä–º—ñ–Ω –≤–∞–≥—ñ—Ç–Ω–æ—Å—Ç—ñ? –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –∫—É–π –ù–∞—Ö—É—è –∫–æ–∑—ñ –±–∞—è–Ω? –í–æ–Ω–∞ —ñ —Ç–∞–∫ –º–µ–∫–∞—î.. –¢—É—è –•–µ—Ä –±—É–π –¥—É–π –∂—É–π –∫—É–π —Å—É–π —Ç—É–π —Ö–∞–π —Ö—É–¥ —Ö—É–ª —á—É–π" },
