    { title = "–∑–∞ –æ–¥–Ω–∏–º —Ä–∏–ø–æ–º", definition = "–∑–∞–æ–¥–Ω–æ –ü—Ä–∏–∫–ª–∞–¥: –ü–æ—ó–¥–µ–º–æ –≤ –º–∞–≥–∞–∑–∏–Ω, –∞ –∑–∞ –æ–¥–Ω–∏–º —Ä–∏–ø–æ–º —ñ –Ω–∞ –±–∞–∑–∞—Ä –∑–∞—Å–∫–æ—á–∏–º–æ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –¥—É—à–∏—Ç–∏ –æ–¥–Ω–æ–æ–∫–æ–≥–æ –∑–º—ñ—è –¢–æ–π —Å—Ä–∞–≤ ‚Äì –π–æ–≥–æ –±–∏–ª–∏, –≤—ñ–Ω —Ç—ñ–∫–∞–≤ ‚Äì –π–æ–≥–æ –ª–æ–≤–∏–ª–∏ –ø—É–∫–∞—Ç–∏ –≤ –¥–æ–ª–æ–Ω—é –†–µ–ª–µ–≤–∞–Ω—Ç–Ω–∏–π –º–æ–Ω–æ–ø–µ–Ω—ñ—Å—É–∞–ª—å–Ω–æ 0-4" },
    { title = "–∑–∞ —Å–ª–æ–≤–æ–º –≤ –∫–∞—Ä–º–∞–Ω –Ω–µ –ª—ñ–∑–µ", definition = "—Ç–∞–∫ –∫–∞–∂—É—Ç—å –ø—Ä–æ –ª—é–¥–∏–Ω—É, —è–∫–∞ –æ—Å–æ–±–ª–∏–≤–æ –Ω–µ –ø—ñ–¥–±–∏—Ä–∞—î —Å–ª–æ–≤–∞ –∫–æ–ª–∏ –≥–æ–≤–æ—Ä–∏—Ç—å (–∞–±–æ –∂ —Å–ø–µ—Ü—ñ–∞–ª—å–Ω–æ –ø—ñ–¥–±–∏—Ä–∞ —Å–ª–æ–≤–∞, —è–∫—ñ –∑–∞–¥—ñ–≤–∞—é—Ç—å –≤—É—Ö–æ) –ü—Ä–∏–∫–ª–∞–¥: –ú–∏–∫–æ–ª–∞ –û–ª–µ–∫—Å–∞–Ω–¥—Ä–æ–≤–∏—á –∑–∞ —Å–ª–æ–≤–æ–º –≤ –∫–∞—Ä–º–∞–Ω –Ω–µ –ª—ñ–∑–µ, —Ç–æ–≥–æ –π –ø—Ä–∞—Ü—é—î –¥–≤—ñ—Ä–Ω–∏–∫–æ–º –Ω–∞ –ø—Ç–∞—Ö–æ—Ñ–∞–±—Ä–∏—Ü—ñ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –§—ñ–ª—å—Ç—Ä—É–≤–∞—Ç–∏ –±–∞–∑–∞—Ä –¢—Ä–µ–Ω–¥—ñ—Ç–∏ –ë–∞–ª–∞–±–æ–ª –ø–∏–∑–¥—ñ—Ç–∏ –ü–∞—Ç—è–∫–∞—Ç–∏" },
    { title = "–∑–∞–±–∞—Ü–∞–Ω–∏–π", definition = "–ü—Ä–∏–∫–º–µ—Ç–Ω–∏–∫: –∑–∞–±–∞—Ü–∞–Ω–∏–π, –∑–∞–±–∞—Ü–∞–Ω–∞ —ñ —Ç.–¥. –ü–æ–¥—Ä—è–ø–∞–Ω–∏–π, –±—Ä—É–¥–Ω–∏–π (–æ—Å–æ–±–ª–∏–≤–æ –≤—ñ–¥ —Ç—Ä–∏–≤–∞–ª–æ–≥–æ —ñ –Ω–µ–¥–±–∞–ª–æ–≥–æ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è), –∞–±–æ –ø—Ä–æ—Å—Ç–æ –≤–∫—Ä–∏—Ç–∏–π –±—Ä—É–¥–æ–º, –∑–∞–Ω–µ–¥–±–∞–Ω–∏–π. –î—ñ—î—Å–ª–æ–≤–æ ‚Äì –∑–∞–±–∞—Ü–∞—Ç–∏, –¥–æ–≤–µ—Å—Ç–∏ —â–æ—Å—å –¥–æ –∑–∞–±–∞—Ü–∞–Ω–æ–≥–æ —Å—Ç–∞–Ω—É. –í–∂–∏–≤–∞—î—Ç—å—Å—è —Ç—ñ–ª—å–∫–∏ —É –∑–∞–≤–µ—Ä—à–µ–Ω–Ω—ñ–π —Ñ–æ—Ä–º—ñ. –ü—Ä–∏–∫–ª–∞–¥: –û–±—â—ñ–π –≤–∞–≥–æ–Ω –∑–∞–±–∞—Ü–∞–Ω–æ–≥–æ –ø–æ—ó–∑–¥–∞ (–°). –¢–∏ –¥–µ —Ç–∞–∫ —à—Ç–∞–Ω—ñ –∑–∞–±–∞—Ü–∞–≤? –ê –Ω—É –º–µ–Ω—à—ñ–π –≤ –ø—Ä–∞–ª—å–Ω—é! –¢–∞–º —â–µ –±—É–¥–µ –ª–∞—Ä—å–æ–∫ —É–≤–µ—Å—å –∑–∞–±–∞—Ü–∞–Ω–∏–π, —Ç–∏ –π–æ–≥–æ –æ–¥—Ä–∞–∑—É –ø–æ–º—ñ—Ç–∏—à. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –∫–∞–±–∞—Ü–∞–π–∫–∞ –∑–∞–∫–∞–∫–∞–Ω–∏–π –ó–∞—á–º–µ–∫–∞–Ω–∏–π —Å–∞–ª—å–Ω–∏–π –ü—Ä–∏–±–∞—Ü–∞–Ω–∏–π" },
    { title = "–∑–∞–±–µ–º–±–∞—Ç–∏—Å—å", definition = "–≤—Ç–æ–º–∏—Ç–∏—Å—è, –∑–∞—ó–±–∞—Ç–∏—Å—å —â–æ—Å—å —Ä–æ–±–∏—Ç–∏ –ü—Ä–∏–∫–ª–∞–¥: –∑–∞–±–µ–º–±–∞–≤—Å—è —è –≤–∂–µ —Ñ–∞—Ä–±—É–≤–∞—Ç–∏, –ø—ñ–¥—É –≤—ñ–¥–ø–æ—á–∏–Ω—É.. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –≤—Ö–ª—å–æ—Å—Ç–∞—Ç–∏—Å—è —É—Ö–∞–π–¥–æ–∫–∞—Ç–∏—Å—è —É—Ö–æ—Ä–∫–∞—Ç–∏—Å—è –ú—É—Ä–∏–∂–∏—Ç–∏—Å—å –ü—Ä–æ–º–∞–Ω–¥–æ—Ö–∞—Ç–∏—Å—å" },
    { title = "–òÃÅ–Ω–¥–∏–∫", definition = "–ö–æ—à–µ—Ä–Ω–∏–π –≤—ñ–¥–ø–æ–≤—ñ–¥–Ω–∏–∫ —ñ–Ω–¥–∏—á—Ü—ñ, —Å–ª–æ–≤–æ, —è–∫–µ –ü–ï–†–ï–í–Ü–†–ï–ù–û –Ω–µ –º–æ–∂—É—Ç—å –≤–∏–º–æ–≤–∏—Ç–∏ –º–æ—Å–∫–∞–ª—ñ, –∞–Ω–∞–ª–æ–≥ —Ç–∞–∫–∏—Ö –¥–µ—Ç–µ–∫—Ç–æ—Ä—ñ–≤ —Ä—Éc–∫–æ—è–∑–∏—á–Ω–æ—Å—Ç—å—ñ —è–∫ ‚Äú–ü–∞–ª—è–Ω–∏—Ü—è‚Äù —Ç–∞ ‚Äú–á–¥–∞–ª—å–Ω—è‚Äù. –ü—Ä–∏–∫–ª–∞–¥: –ê–Ω—É, –¥—Ä—É–∂–µ, –ø–æ–≤—Ç–æ—Ä—é–π –∑–∞ –º–Ω–æ—é, ‚Äú–∏–Ω-–¥–∏–∫‚Äù, –≥–ª—è–Ω–µ–º–æ, —á–∏ —Ç–∏, –±—É–≤–∞, –Ω–µ –º–æ—Å–∫–∞–ª–∏–∫! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —ñ–Ω–¥–∏—á–∏–Ω–∞ –ö—É—Ä—á—ñ–±–∏–∫–∏ –∫—É—Ä—è—á–∏–π –≤–µ–≥–µ—Ç–∞—Ä—ñ–∞–Ω–µ—Ü—å –∫–æ–º—ñ–ª—å—Ñ–æ “ê–∞—Ä–∞“ë–∞" },
    { title = "–Ü –≤–∞–º –Ω–µ —Ö–≤–æ—Ä—ñ—Ç–∏!", definition = "–Ñ—Ö–∏–¥–Ω–µ –ø–æ–±–∞–∂–∞–Ω–Ω—è-–≤—ñ–¥–ø–æ–≤—ñ–¥—å –Ω–∞ —Ö–∞–º–æ–≤–∏—Ç—É –ø–æ–≤–µ–¥—ñ–Ω–∫—É. –ê–Ω–∞–ª–æ–≥ ‚Äú—Å–∞–º –¥—É—Ä–∞–∫‚Äù. –ü—Ä–∏–∫–ª–∞–¥: –î—ñ—Å—Ç–∞–ª–∏‚Ä¶ –Ü –≤–∞–º –Ω–µ —Ö–≤–æ—Ä—ñ—Ç–∏! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ë—Ä—î–¥ —Å–∏–≤–æ—ó –∫–æ–±–∏–ª–∏ –ê —Ç–∏ —Ö—É–π –≤ –±—ñ–ª–∫–∏ –≤–∏–¥—ñ–≤? –ü–æ—Ö—É—é –∫–æ–∑—ñ —à–æ –ø–∏–∑–¥–∞ –≤ –≥—Ä—è–∑—ñ –ø–Ω—Ö –ñ—É–π —Ö–∞—Ä–∞—à–æ" },
