    { title = "–ô–æ–±–∞–Ω–∞ –≤ —Ä–æ—Ç", definition = "1) –°–ª–æ–≤–æ—É—Ç–≤—ñ—Ä-–≤–∏–≥—É–∫ –Ω–∞ –æ–∑–Ω–∞–∫—É –ø–æ–¥–∏–≤—É –ª—é–¥–∏–Ω–∏ –≤—ñ–¥ —Ç–æ–≥–æ, —â–æ –≤—ñ–¥–±—É–≤–∞—î—Ç—å—Å—è; 2) –ó–Ω–µ–≤–∞–∂–ª–∏–≤–µ —Å—Ç–∞–≤–ª–µ–Ω–Ω—è –¥–æ –∂—ñ–Ω–∫–∏ —è–∫ –¥–æ –º—ñ–Ω—å—î—Ç—á—ñ—Ü–∏ –ü—Ä–∏–∫–ª–∞–¥: 1) –ô–æ–±–∞–Ω–∞ –≤ —Ä–æ—Ç, –í–∞—Å—è–Ω—è, –Ω–µ —Ç—É–ø—ñ! 2) –¢–∞ —Ü—è —Ç—å–æ–ª–∫–∞ –π–æ–±–∞–Ω–∞ –≤ —Ä–æ—Ç, –∞—Ç–≤—ñ—á–∞—é! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –≥–æ—Å–ø–æ–¥–∏ –ø—Ä–∞–≤–µ–¥–Ω–∏–π! –ü–∏–∑–¥–æ–±–ª—è–¥—Å—å–∫–∏–π –ú–∞–ª–∞—è –á–±–∞–ª–∞ –∂–∞–±–∞ –≥–∞–¥—é–∫—É –ë—É–ª–æ –≤—Ä–µ–º—è—á–∫–æ, —ó–ª–∞ –∂–æ–ø–∞ —Å–µ–º—è—á–∫—É, –∞ —Ç–µ–ø–µ—Ä —ñ –≤ —Ä–æ—Ç –Ω–µ –¥–∞—é—Ç—å." },
    { title = "–π–æ–±–∞–Ω–∏–π —Å–∞—Ä–∞–π!", definition = "–í–∏–≥—É–∫, —è–∫–∏–π –ª—É–Ω–∞—î –≤—ñ–¥ —è–∫–æ—ó—Å—å –ª—é–¥–∏–Ω–∏ –≤—ñ–¥ –∑–¥–∏–≤—É–≤–∞–Ω–Ω—è. –ü—Ä–∏–∫–ª–∞–¥: –í—á–∏—Ç–µ–ª—å–∫–∞: –ô–æ–±–∞–Ω–∏–π —Å–∞—Ä–∞–π! –í—ñ—Ç–∞–ª—ñ–∫! –Ω–∞—Ö–µ—Ä–∞ —Ç–∏ —à–∫—ñ–ª—å–Ω–∏–π –∂—É—Ä–Ω–∞–ª —Å–ø–∞–ª–∏–≤?! –¢—Ä–µ–±–∞ –±—É–ª–æ –ø—ñ–¥–ø–∞–ª—é–≤–∞—Ç–∏ —Ö–æ—á–∞ –± –Ω–µ –≤ –∫–ª–∞—Å—ñ! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –π–æ—Å–∏–ø –±–æ—Å–∏–π! —ó–±–∞—Ç—å –º—ñ–π —Ö—É–π –í —Ä–æ—Ç –º–µ–Ω—ñ –Ω–æ–≥–∏ —Å—Ä–∞–∫–∞-–º–æ—Ç–∏–∫–∞, –≥—ñ–º–Ω–æ-–∫–∞–º—ñ–∑–µ–ª—å–∫–∞ —Å—Ä–∞–∫–∞-–±–∞–Ω—å–∫–∞" },
    { title = "–π–æ–±–∞–Ω–∏–π —Å—Ç–∏–¥", definition = "–π–æ–±–∞–Ω–∏–π —Å—Ç–∏–¥ –∞–±–æ –π–æ–±–∞–Ω–∏–π —Å–æ—Ä–æ–º ‚Äì —Å–∏—Ç—É–∞—Ü—ñ—è –∫—Ä–∞–π–Ω—å–æ–≥–æ —Å–æ—Ä–æ–º—É —ñ –≥–∞–Ω—å–±–∏ –ü—Ä–∏–∫–ª–∞–¥: –ø–æ—ó—Ö–∞–≤ –Ω–∞ —Ä–∏–±–∞–ª–∫—É —ñ –ø–æ–π–Ω—è–≤—à–æ –∑–∞–±—É–≤ —è–∫ –∑–∞–∫–∏–¥—É–≤–∞—Ç—å —Å–ø—ñ–Ω—ñ–Ω–≥. –π–æ–±–∞–Ω–∏–π —Å–æ—Ä–æ–º! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –†—É–∫–∞–æ–±–ª–∏—á—á—è –ü–µ–∫—Ç–∏ —Ä–∞–∫—ñ–≤ –ô–æ–±–∞–Ω–∞ –≤ —Ä–æ—Ç –á–±–∞–ª–∞ –∂–∞–±–∞ –≥–∞–¥—é–∫—É –π–æ–±–∞–Ω–∏–π —Ü–µ–π–≤–æ!" },
    { title = "–ô–æ–±–∞–Ω–∏–π —Å—É—Ç—É–ª–∏–π —Ö—É–π!", definition = "–í–∏–≥—É–∫, —è–∫–∏–π –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É—î—Ç—å—Å—è –ø—Ä–∏ –∫—Ä–∞–π–Ω—å–æ–º—É –∑–¥–∏–≤—É–≤–∞–Ω–Ω—ñ. –ü—Ä–∏–∫–ª–∞–¥: ‚Äì –ô–æ–±–∞–Ω–∏–π —Å—É—Ç—É–ª–∏–π —Ö—É–π! –¢–∏ –Ω–∞—Ö—É—è —Ç—É–¥–∏ –ø–æ–ª—ñ–∑? –ó–æ–≤—Å—ñ–º –π–æ–±–Ω—É–≤—Å—è –Ω–∞ –≤—Å—é –≥–æ–ª–æ–≤—É? –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ô–æ–±–∞–Ω–∏–π-–ø–æ–π–æ–±–∞–Ω–∏–π –°—Ä–∞–∫–∞-–¥—Ä–æ–≤–∏–Ω—è–∫–∞ –Ø–ø–æ–Ω—Å—å–∫–∏–π –∫—ñ–Ω—å –í —Ä–æ—Ç –º–µ–Ω—ñ –Ω–æ–≥–∏ –á–±–∞–Ω—É—Ç—å—Å—è!" },
    { title = "–ô–æ–±–∞–Ω–∏–π —Ü–µ–π–≤–æ", definition = "–Ω–∞–ø—ñ–≤–µ–≤—Ñ–µ–º—ñ–∑–º –≤–∏—Ä–∞–∑—É ‚Äú–π–æ–±–∞–Ω–∏–π —Ö—É–π‚Äù. –Ω–∞–π–±—ñ–ª—å—à–≤ –≤—ñ–¥–æ–º–∏–π –ø—Ä–∏–∫–ª–∞–¥ –≤–∂–∏–≤–∞–Ω–Ω—è –ø–æ–∫–∞–∑–∞–Ω–æ –Ω–∏–∂—á–µ: –ü—Ä–∏–∫–ª–∞–¥: —Å—É–∫–∞ –±–ª—è—Ç—å –π–æ–±–∞–Ω–∏–π —Ü–µ–π–≤–æ. —Å—É—Ñ–ª—å–æ—Ä –±–ª—è—Ç—å! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ô–æ–±–∞–Ω–∞ –≤ —Ä–æ—Ç –ü–∏–∑–¥–∞-–Ω–æ–≥–∞ –á–¥—Ä—ñ—Ç—å —Ç–≤–æ—é –º–∞—Ç—å –π–æ—Ö–∞–Ω–∏–π –±–∞–±–∞–π! –π–æ–±–∞–Ω–∏–π —Ü–µ–π–≤–æ!" },
    { title = "–ö", definition = "–æ–∑–Ω–∞—á–∞—î ‚Äú–∫—É—Å–æ–∫‚Äù —Ç–æ–±—Ç–æ —Ç–∏—Å—è—á—É –ü—Ä–∏–∫–ª–∞–¥: ‚Äì 2–ö —Ü–µ –Ω–µ –≥—Ä–æ—à—ñ ‚Äì –Ω—É –¥–∏–≤–ª—è—á–∏—Å—å —á–æ–≥–æ.. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –õ–∏–º–æ–Ω –¢–∏—Å—è—á–æ–Ω–µ—Ä –ö–µ—à –ó–µ–ª–µ–Ω–∏–π –ì—Ä–∏–≤–Ω—è" },
    { title = "–ö —î–±—î–Ω—ñ –º–∞—Ç–µ—Ä—ñ", definition = "—Å—Ç–∞–ª–∏–π –≤–∏—Ä–∞–∑, —â–æ –∑–∞–π–º–∞—î –º—ñ—Å—Ü–µ –¥–µ—Å—å –ø–æ–º—ñ–∂ –Ω–∞—Ö—É–π —ñ –ø—ñ–¥ —Ç—Ä–∏ —á–æ—Ä—Ç–∏. –ü—Ä–∏–∫–ª–∞–¥: –ù–µ –≤–ø–æ—Ä–∞–≤—à–∏—Å—å –∑ –∫–µ—Ä—É–≤–∞–Ω–Ω—è–º –°–∞—à–∫–æ –≤—Ä—ñ–∑–∞–≤—Å—è –≤ –¥–µ—Ä–µ–≤–æ, —ñ —Ä–æ–∑‚Äô—ó–±–∞–≤ –±–∞—Ç—å–∫–æ–≤—É –∫–æ–ø—ñ–π–∫—É –∫ —î–±—î–Ω—ñ –º–∞—Ç–µ—Ä—ñ. –ú–µ–Ω—ñ –º—ñ–π –ø–æ–º—ñ—á–Ω–∏–∫ –ê–ª—î–∫—Å–∞–Ω–¥—Ä–æ–≤-–ê–≥–µ–Ω—Ç–æ–≤ —Ä–æ–∑–ø–æ–≤—ñ–¥–∞–≤, —â–æ –ü–µ—Ç—Ä–æ –ü–µ—Ä—à–∏–π –Ω–∞ –¥–æ–Ω–æ—Å–∞—Ö –¢–∞—î–º–Ω–æ—ó –∫–∞–Ω—Ü–µ–ª—è—Ä—ñ—ó –ø—Ä–æ –∞–Ω—Ç–∏–¥–µ—Ä–∂–∞–≤–Ω—É –¥—ñ—è–ª—å–Ω—ñ—Å—Ç—å –±–æ—è—Ä –ø–∏—Å–∞–≤ —Ä–µ–∑–æ–ª—é—Ü—ñ—ó: ¬´–ö —î–±—î–Ω—ñ –º–∞—Ç—î—Ä—ñ¬ª, —â–æ –æ–∑–Ω–∞—á–∞–ª–æ ‚Äì ¬´–í—ñ–¥–ø—Ä–∞–≤–∏—Ç–∏ –Ω–∞ –°–æ–ª–æ–≤–∫–∏¬ª. –ü–æ—Ç—ñ–º –π–æ–º—É –Ω–∞–±—Ä–∏–¥–ª–æ –ø–∏—Å–∞—Ç–∏ —Ç–∞–∫—ñ –¥–æ–≤–≥—ñ —Ä–µ–∑–æ–ª—é—Ü—ñ—ó, —ñ –≤—ñ–Ω –¥–∞–≤–∞–≤ –≤–∫–∞–∑—ñ–≤–∫–∏ –∫–æ—Ä–æ—Ç–∫–æ, –∞–±—Ä–µ–≤—ñ–∞—Ç—É—Ä–æ—é: ¬´–ö–Ñ–ú¬ª. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –®—É—Ä—É–≤–∞—Ç–∏ –•—É–π–æ–≤–∏–π –ù–∞—Ö—É–π –ü–∏–∑–¥–æ–≤–∞—Ç–∏–∑–º" },
    { title = "–ö–∞–±–∞–∫–∞", definition = "—Ç–µ —Å–∞–º–µ —à–æ —Ö—É—è–∫–∞ ‚Äì –≤–µ–ª–∏–∫–∏–π —Å–æ–∫–æ–≤–∏—Ç–∏–π —á–ª–µ–Ω. –ü—Ä–∏–∫–ª–∞–¥: –ê–Ω–¥—Ä—ñ–π –ø—Ä–æ—Å–ª–∞–≤–∏–≤—Å—è –Ω–∞ –≤—Å–µ —Å–µ–ª–æ –∑–∞–≤–¥—è–∫–∏ —Å–≤–æ—ó–π –Ω–µ–≤‚Äô—î–±–µ–∑–Ω—ñ–π –∫–∞–±–∞—Ü—ñ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ë–æ–ª—Ç –ú–∞—Ç—É–º–±–∞ –º–æ–Ω–æ–ø–µ–Ω—ñ—Å—É–∞–ª—å–Ω–æ –î—Ä–æ—á–∏–ª–æ –•—É–π" },
    { title = "–∫–∞–±–∞–Ω—ñ", definition = "–¢–µ —Å–∞–º–µ, —â–æ –∫–∞–±–∞—á–∫–∏ –ü—Ä–∏–∫–ª–∞–¥: –°—å–æ–≥–æ–¥–Ω—ñ —Å–º–∞–∂–∏–ª–∞ –∫–∞–±–∞–Ω—ñ. –î—É–∂–µ —Å–º–∞—á–Ω–æ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —Ç–∞–º–±–æ–Ω–∏ –î–∏–Ω—è –°–∏–Ω–µ–Ω—å–∫—ñ –ö–∞–±–∞–∫–∞ –ì—É–∞–∫–∞–º–æ–ª–µ" },
    { title = "–∫–∞–±–∞—Ü–∞–π–∫–∞", definition = "—Ç–µ —Å–∞–º–µ, —â–æ –π –∫–∞—Ü–∞–±–∞–π–∫–∞ ‚Äî —Ç–µ–ø–ª–∞ –∂–∏–ª–µ—Ç–∫–∞ –ü—Ä–∏–∫–ª–∞–¥: –ì–∞–Ω—É—Å—é, –ø—Ä–∏–Ω–µ—Å–∏ –∫–∞–±–∞—Ü–∞–π–∫—É, —â–æ—Å—å —è –∑–º–µ—Ä–∑–ª–∞ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –∫—ñ–≥—É—Ä—É–º—ñ –∫–∞—Ü–∞–±–∞–π–∫–∞ –í–∞—Ç–Ω–∏–∫ –±—Ä–æ–Ω—ñ–∫ —Ç—É–Ω—ñ–∫–∞" },
    { title = "–∫–∞–±–∞—á–µ—ái", definition = "–∫–∞–±–∞—á–∫–∏, –∫–∞–±–∞—á–æ–∫, –∫–∞–±–∞–∫ ‚Äì —Ä–æ—Å–ª–∏–Ω–∞ —Åi–º–µ–π—Å—Ç–≤–∞ –≥–∞—Ä–±—É–∑–æ–≤–∏—Ö, –º–∞—î –ø—Ä–æ–¥–æ–≤–≥—É–≤–∞—Å—Ç—É —Ñ–æ—Ä–º—É —Å–≤i—Ç–ª–æ-–∑–µ–ª–µ–Ω–∏–π –∫–æ–ªi—Ä. –ü—Ä–∏–∫–ª–∞–¥: –•–æ—á—É –∫—É–ø–∏—Ç–∏ —Ç–∞ –ø–æ–∂–∞—Ä–∏—Ç–∏ (–ø–æ—Å–º–∞–∂–∏—Ç–∏) –∫–∞–±–∞—á–µ—ái. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –¥—Ä–∏—Å–ª—ñ–Ω–∫–∏ –∫–∞–±–∞–Ω—ñ —Ç–∞–º–±–æ–Ω–∏ –°–∏–Ω–µ–Ω—å–∫—ñ –ö–∞–±–∞–∫–∞" },
    { title = "–∫–∞–±–∑–¥–µ—Ü—å", definition = "—â–æ—Å—å —Å–µ—Ä–µ–¥–Ω—î –º—ñ–∂ ‚Äú–∫–∞–ø–µ—Ü—å‚Äù —Ç–∞ ‚Äú–ø–∏–∑–¥–µ—Ü—å‚Äù. –∞ —Å–∞–º–µ: 1. –ø—Ä–æ —â–æ—Å—å –Ω–∞–¥–∑–≤–∏—á–∞–π–Ω–µ, –∑–∞–∑–≤–∏—á–∞–π –Ω–µ–≥–∞—Ç–∏–≤–Ω–µ; —Å–∏–Ω–æ–Ω—ñ–º —Å–ª–æ–≤–∞ ‚Äú–∂–∞—Ö‚Äù 2. –∫—ñ–Ω–µ—Ü—å, –∑–∞–∑–≤–∏—á–∞–π –Ω–µ–≤–¥–∞–ª–∏–π –ü—Ä–∏–∫–ª–∞–¥: –í–∏–±–∞—á—Ç–µ –º–µ–Ω–µ, –∞–ª–µ —Ü–µ –∫–∞–±–∑–¥–µ—Ü—å. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ê —ó–±–∏—Å—å —Ç–∏ –ø—Ä–æ–≤–∞–ª–∏—Å—å! –≥–∞–ø–ª–∏–∫ –∫–∏–∑–¥–∞ –ö–∞–ø–µ—Ü—å –ü–∏–∑–¥–µ—Ü—å" },
    { title = "–ö–∞–±–ª–∏", definition = "–ö–∞–±–ª—É–∫–∏, –ø—ñ–¥–±–æ—Ä–∏ –ü—Ä–∏–∫–ª–∞–¥: –ù–∞ —Ü—é –≤–µ—á—ñ—Ä–∫—É –ø—Ä–∏–π–¥–µ—Ç—å—Å—è –≤–∑—É—Ç—å –∫–∞–±–ª–∏. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —à–∫–∞—Ä–∏ –ê–≤—Ç–æ–ø‚Äô—è—Ç–∫–∞ –Ω–æ—Å–æ–∂–∫–∏ —á—É–≤—è–∫–∏ –ì—ñ–≤–Ω–æ–¥–∞–≤–∏" },
    { title = "–ö–∞–±–æ—Ç–∏–Ω—Å—Ç–≤–æ", definition = "–°—Ö–∏–ª—å–Ω—ñ—Å—Ç—å –¥–æ –∞–∫—Ç–æ—Ä—Å—Ç–≤–∞ —É –Ω–∞–π–≥—ñ—Ä—à–æ–º—É –∑–Ω–∞—á–µ–Ω–Ω—ñ —Ü—å–æ–≥–æ —Å–ª–æ–≤–∞, –ø–æ–∑–µ—Ä—Å—Ç–≤–æ, –ø—Ä–∞–≥–Ω–µ–Ω–Ω—è –¥–æ —Å–ª–∞–≤–∏. –ü—Ä–∏–∫–ª–∞–¥: –í–∏—Å—Ç—É–ø–∏ –ü–æ—Ä–æ—à–µ–Ω–∫–∞ —á–∞—Å—Ç–æ ‚Äú–≤—ñ–¥–¥–∞—é—Ç—å‚Äù –∫–∞–±–æ—Ç–∏–Ω—Å—Ç–≤–æ–º. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –∫–∞–±–∞–Ω—ñ –ö–∞–±–ª–∏ –ê—Ä—Ç-–º–æ–±, –º–æ–±-–∞—Ä—Ç –°–µ–ª–µ–±–ª—è–¥—ñ –ö–∞–±–∞–∫–∞" },
    { title = "–õ–∞–±–∞", definition = "–ª–∞–±–æ—Ä–∞—Ç–æ—Ä–Ω–∞ —Ä–æ–±–æ—Ç–∞ –ü—Ä–∏–∫–ª–∞–¥: –¢—Ä–µ–±–∞ —Ä–∞–Ω–æ –≤—Å—Ç–∞—Ç–∏, –ª–∞–±–∞ –Ω–∞ –ø–µ—Ä—à—ñ–π –ø–∞—Ä—ñ. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –®–æ–∫–æ–ª–∞–¥–Ω–∏—Ü—è –ü–ª–∞–Ω –®–æ —É–ø–∞–ª–æ —É —Å—Ç—É–¥–µ–Ω—Ç–∞, —Ç–æ —É–ø–∞–ª–æ –Ω–∞ –≥–∞–∑–µ—Ç—É" },
    { title = "–õ–∞–±–∞—Å—å", definition = "–î—É—Ä–µ–Ω—å, –Ω–µ–¥–∞–ª–µ–∫–∞ –ª—é–¥–∏–Ω–∞ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –®–ª–æ–π–±–∞–Ω —ó–±–∞–Ω—å–∫–æ –ø–æ–¥–∞–π –ø–∞—Ç—Ä–æ–Ω–∏ –±–∞–º–±—É–ª–∞ “ê–∞—Ä–∞“ë–∞ –ü—Ä–∏—Ü—é—Ü—å–æ–∫" },
    { title = "–ª–∞–±–∞—Ç–∏", definition = "–ì—Ä–∞—Ç–∏ –º—É–∑–∏–∫—É,–Ω–µ –¥–æ—Ç—Ä–∏–º—É—é—á–∏—Å—å –Ω–æ—Ç –ü—Ä–∏–∫–ª–∞–¥: –ü–µ—Ä–µ—Å—Ç–∞–Ω—å –ª–∞–±–∞—Ç–∏‚Ä¶–î–∏–≤–∏—Å—è —É –Ω–æ—Ç–∏. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –£–≥—Ä–∞—Ç–∏ –õ–∞–±—É–¥–∞ –º–µ—Ç–∞–ª –õ–∞–±–∞ –®–ø—ñ–ª–∏—Ç–∏" },
    { title = "–õ–∞–±—É–¥–∞", definition = "–§—ñ–≥–Ω—è, —á–µ–ø—É—Ö–∞, —î—Ä—É–Ω–¥–∏—Å—Ç–∏–∫–∞, –Ω—ñ—Å–µ–Ω—ñ—Ç–Ω–∏—Ü—è. –ü–æ—Ö–æ–¥–∏—Ç—å –≤—ñ–¥ –ª–∞–±—É—Ö (–º—É–∑–∏–∫–∞–Ω—Ç, —â–æ –≥—Ä–∞—î –Ω–µ–∫–ª–∞—Å–∏—á–Ω—É –º—É–∑–∏–∫—É), —Ç–æ–±—Ç–æ –ª–∞–±–∞—î. –ü—Ä–∏–∫–ª–∞–¥: –Ø–∫–∞—Å—å –ª–∞–±—É–¥–∞. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –±–∑–¥—É—Ä–∞ —á–æ—Ä—Ç—ñ—à–æ –ß—É—Ö–Ω—è –•—É–π–Ω—è –§—ñ–≥–Ω—è" },
