    { title = "–ª–∞–≤–µ", definition = "–ª–∞–≤–µ —Ü–µ –±–∞–±–æ—Å, —Ç–æ–±—Ç–æ –≥—Ä–æ—à—ñ –ü—Ä–∏–∫–ª–∞–¥: –≤ –º–µ–Ω–µ –∑–Ω–æ–≤ –Ω–µ–º–∞ –ª–∞–≤–µ, –≤—Å–µ —Å–ø—É—Å—Ç–∏–≤ —è –ø–æ –ø—ñ–∑–¥–µ.. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ö–∞–ø—É—Å—Ç–∞ –∞—Å–∫–∞—Ç–∏ –Ω–∞ –Ω—É–ª—è—Ö Bablo –ö–µ—à" },
    { title = "–ª–∞–≤–æ—á–∫–∞", definition = "–ª–∞–≤–æ—á–∫–∞ ‚Äì —Ü–µ –º—ñ—Å—Ü–µ –≤ —Å–µ–ª—ñ, –¥–µ –∑–±–∏—Ä–∞—î—Ç—å—Å—è –º–æ–ª–æ–¥—å —É–≤–µ—á–µ—Ä—ñ. –ø–∞—Ü–∞–Ω–∏ —ñ –¥—î–≤–∫–∏ —Å—é–¥–∏ –ø—Ä–∏—Ö–æ–¥—è—Ç—å —ñ–∑ –ø–∏–≤–æ–º, —Ñ–∞–Ω—Ç–æ—é —ñ –Ω–∞—Å—ñ–Ω–Ω—è–º (—Ä–∞–Ω—ñ—à–µ —Å–∞–º—ñ —Å–º–∞–∂–∏–ª–∏, —Ç–µ–ø–µ—Ä —î –ø–∞—á–∫–æ–≤–µ). —Å—é–¥–∏ –Ω–µ –æ–±–æ–≤‚Äô—è–∑–∫–æ–≤–æ –ø—Ä–∏—Ö–æ–¥–∏—Ç–∏ –Ω–∞—Ä—è–¥–Ω–∏–º, –∞–ª–µ —Ü–µ —Ç—ñ–ø–∞ —è–∫ –ø—Ä–∞–≤–∏–ª–æ ‚Äú–ø–æ —É–º–æ–ª—á–∞–Ω—ñ—é‚Äù. —Å—é–¥–∏ –ø—Ä–∏—ó–∂–¥–∂–∞—é—Ç—å –Ω–∞ –º–æ—Ç–æ—Ü–∏–∫–ª–∞—Ö —ñ –ø—Ä–∏—Ö–æ–¥—è—Ç—å –ø—ñ—à–∫–æ–º. —Ç—ñ, —à–æ –Ω–∞ –º–æ—Ç–æ—Ü–∏–∫–ª–∞—Ö, —Ä–æ–∑–∫–∞–∂—É—Ç—å, —à–æ —Å—å–æ–≥–æ–¥–Ω—ñ –≤ —Å—É—Å—ñ–¥–Ω—å–æ–º—É —Å–µ–ª—ñ –º–∞—î –±—É—Ç–∏ –∫—Ä—É—Ç–æ, —ñ –≤—ñ–∑—å–º—É—Ç—å —â–∞—Å–ª–∏–≤—á–∏–∫—ñ–≤ —ñ–∑ —Å–æ–±–æ—é. –∞ –æ—Å—Ç–∞–ª—å–Ω–∏–º —ñ —Ç—É—Ç –±—É–¥–µ –Ω–µ–ø–æ–≥–∞–Ω–æ. –º–æ–∂–µ, —Ö—Ç–æ—Å—å —ñ –ø–æ–∑–∞–≤—ñ–¥—É—î, –∞–ª–µ —Ç—ñ–ª—å–∫–∏ –Ω–∞ 5 —Å—î–∫. —Ç—É—Ç –±–∞–≥–∞—Ç–æ –≥–æ–≤–æ—Ä—è—Ç—å –ø—Ä–æ —Ä–∏–±–∞–ª–∫—É, –ª—é–±–æ–≤ —ñ –º–∞–π–±—É—Ç–Ω—î. –≤–µ–¥—É—Ç—å—Å—è –Ω–∞–π–º–∏–ª—ñ—à—ñ —Å–µ—Ä—Ü—é —Ä–æ–∑–º–æ–≤–∏ —ñ —Å–∫–ª–∞–¥–∞—é—Ç—å—Å—è –Ω..." },
    { title = "–ª–∞–≥–∞—Ç–∏", definition = "–∫–æ–º–ø—å—é—Ç–µ—Ä–Ω–∏–π —Å–ª–µ–Ω–≥; –Ω–µ—Å–ø—Ä–æ–º–æ–∂–Ω—ñ—Å—Ç—å –º–∞—à–∏–Ω–∏ –≤—á–∞—Å–Ω–æ —Ä–µ–∞–≥—É–≤–∞—Ç–∏ –Ω–∞ –Ω–∞–¥—ñ—Å–ª–∞–Ω—ñ –∑–∞–ø–∏—Ç–∏, –≥–∞–ª—å–º—É–≤–∞–Ω–Ω—è. –∑ –∞–Ω–≥–ª—ñ–π—Å—å–∫–æ—ó lag ‚Äì –≤—Ç—Ä–∞—á–∞—Ç–∏ —Ç–µ–º–ø, –≥–∞–ª—å–º—É–≤–∞—Ç–∏. –ü—Ä–∏–∫–ª–∞–¥: –≤ –ñ–µ–Ω—ñ —Å–ø–æ—á–∞—Ç–∫—É –ø–æ—á–∞–ª–∞ –ª–∞–≥–∞—Ç–∏ –º–∏—à–∫–∞, –ø–æ—Ç—ñ–º –≤—ñ–Ω–¥–∞, –∞ –ø–æ—Ç—ñ–º —ñ —Å–∞–º –∫–æ–º–ø –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –≥–∞–º–∞—Ç–∏ –ö–ª—ñ–∫–∞—Ç–∏ –ê–ø–≥—Ä–µ–π–¥ –ó–∞–≤–∏—Å–∞—Ç–∏ –ö–æ–º–ø—å—é—Ç–µ—Ä–Ω–∞ –º–∞–ª—è—Ä—ñ—è" },
    { title = "–ú‚Äô—è–∫–∞ —Å–∏–ª–∞", definition = "—É –º—ñ–∂–Ω–∞—Ä–æ–¥–Ω—ñ–π –ø–æ–ª—ñ—Ç–∏—Ü—ñ ‚Äî –¥–æ—Å—è–≥–Ω–µ–Ω–Ω—è –¥–µ—Ä–∂–∞–≤–æ—é –±–∞–∂–∞–Ω–∏—Ö –¥–ª—è —Å–µ–±–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ñ–≤ —á–µ—Ä–µ–∑ –∑–∞–æ—Ö–æ—á–µ–Ω–Ω—è —Ç–∞ –ø—Ä–∏–≤–∞–±–ª–∏–≤—ñ—Å—Ç—å —Å–≤–æ—î—ó –º–æ–¥–µ–ª—ñ, –Ω–∞ –ø—Ä–æ—Ç–∏–≤–∞–≥—É –∂–æ—Ä—Å—Ç–∫—ñ–π —Å–∏–ª—ñ ‚Äî —Å–∏–ª—ñ –ø—Ä–∏–º—É—Å—É. –ü—Ä–∏–∫–ª–∞–¥: –ì–æ–ª–æ–≤–Ω–∏–º –∑–Ω–∞—Ä—è–¥–¥—è–º ¬´–º‚Äô—è–∫–æ—ó —Å–∏–ª–∏¬ª –µ–∫—Å–ø–µ—Ä—Ç–∏ –≤–∏–∑–Ω–∞–ª–∏ –±—Ä–∏—Ç–∞–Ω—Å—å–∫–µ —Ç–µ–ª–µ–±–∞—á–µ–Ω–Ω—è, —Ñ—ñ–ª—å–º–∏ —Ç–∞ —Å–µ—Ä—ñ–∞–ª–∏ –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –µ—Ñ–µ–∫—Ç –ü—ñ–≤–Ω—ñ—á–Ω–æ—ó –ö–æ—Ä–µ—ó –§–µ–π–ª–¥ —Å—Ç–µ–π—Ç –ø–æ–ª—ñ—Ü–µ–π—Å—å–∫–∞ –¥–µ—Ä–∂–∞–≤–∞ –ñ–∏–≤–∞ —Å–∏–ª–∞ –î–µ—Ä–∂–∞–≤–Ω–∏–π —Ñ—ñ—Ç–Ω–µ—Å" },
