    { title = "–®–∞–ª–∞–≤–∞", definition = "–¥—ñ–≤—á–∏–Ω–∞ –ª–µ–≥–∫–æ—ó –ø–æ–≤–µ–¥—ñ–Ω–∫–∏. –¥—ñ–≤—á–∏–Ω–∞, —è–∫–∞ –ª–µ–≥–∫–æ –∑–º—ñ–Ω—é—î —Å—Ç–∞—Ç–µ–≤–∏—Ö –ø–∞—Ä—Ç–Ω–µ—Ä—ñ–≤ –ü—Ä–∏–∫–ª–∞–¥: –ö–æ–ª–∏ –Ü–≥–æ—Ä –¥—ñ–∑–Ω–∞–≤—Å—è, —â–æ –Æ–ª—è –∑—Ä–∞–¥–∏–ª–∞ –π–æ–º—É –∑ –°–µ—Ä–≥—ñ—î–º, –≤—ñ–Ω –¥—É–∂–µ –∑–∞—Å–º—É—Ç–∏–≤—Å—è —ñ –Ω–∞–ø–∏—Å–∞–≤ –≤–Ω–æ—á—ñ —Ñ–∞—Ä–±–æ—é –≤–µ–ª–∏—á–µ–∑–Ω–∏–º–∏ –ª—ñ—Ç–µ—Ä–∞–º–∏ –ø–µ—Ä–µ–¥ —ó—ó –≤–æ—Ä–æ—Ç–∞–º–∏ —Å–ª–æ–≤–æ ‚Äú–®–∞–ª–∞–≤–∞‚Äù. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ú–∞–ª–∞—è —Ö—É–Ω–∞ –§–æ–π–¥–∞ –°—Ç–µ—Ä–≤–æ" },
    { title = "–®–∞–ª–∞–π–∫–∞", definition = "–ø–æ–º—ñ—Å—å –®–ê–∫–∞–ª–∞ —Ç–∞ –õ–ê–ô–ö–∏.—Ç–∞–∫–æ–∂ –Ω–∞–∑–∏–≤–∞—é—Ç—å —à–∞–±–∞–∫–∞ = –®–ê–∫–∞–ª + —Å–æ–ë–ê–ö–ê. —Ä—ñ–¥–∫—ñ—Å–Ω–∞ —Ä–æ—Å—ñ–π—Å—å–∫–∞ —Å–ª—É–∂–±–æ–≤–∞ –ø–æ—Ä–æ–¥–∞ –∑ –≤—ñ–¥–º—ñ–Ω–Ω–∏–º –Ω—é—Ö–æ–º. –ü—Ä–∏–∫–ª–∞–¥: –î–ª—è –≤–∏—è–≤–ª–µ–Ω–Ω—è —É –±–∞–≥–∞–∂—ñ –≤–∏–±—É—Ö–æ–≤–∏—Ö —Ä–µ—á–æ–≤–∏–Ω –∫—Ä–∞—â–µ –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ ¬´—à–∞–ª–∞–π–æ–∫¬ª. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —Å–æ–±–∞–∫–æ–≤–±–∏–≤—Ü—è –û—Å—å –¥–µ —Å–æ–±–∞–∫–∞ –ø–æ—Ä–∏–ª–∞—Å—è –ì–∞–≤—á–∏–∫ –¥–æ–≥—Ö–∞–Ω—Ç–µ—Ä–∏ –¥–≤—ñ—Ä-—Ç–µ—Ä‚Äô—î—Ä" },
    { title = "–®–∞–ª–µ–Ω–∏–π –ø—Ä–∏–Ω—Ç–µ—Ä", definition = "–ó–ï—Å–ª—É–≥–∏ –Ω–∞—Ä–æ–¥—É, —è–∫—ñ –Ω–∞ —à–∞–ª–µ–Ω—ñ–π —à–≤–∏–¥–∫–æ—Å—Ç—ñ, –Ω–µ –∑–±–∞–≤–ª—è—é—á–∏ —Ç–µ–º–ø, –ø—Ä–∏–π–º–∞—é—Ç—å –∑–∞–∫–æ–Ω–∏, –∑–º—ñ–Ω–∏ –¥–æ –ö–æ–Ω—Å—Ç–∏—Ç—É—Ü—ñ—ó ‚Äì –Ω–∞—Å—Ç—ñ–ª—å–∫–∏ —à–≤–∏–¥–∫–æ, —â–æ –ø—Ä–æ –Ω—é–∞–Ω—Å–∏ –Ω–æ–≤–∞—Ü—ñ–π –Ω–µ –º–æ–∂—É—Ç—å —Ä–æ–∑–ø–æ–≤—ñ—Å—Ç–∏ –±—ñ–ª—å—à—ñ—Å—Ç—å ‚Äú–∑–µ–ª–µ–Ω–∏—Ö‚Äù –¥–µ–ø—É—Ç–∞—Ç—ñ–≤, —Å–∞–º–∏—Ö –∑–∞–∫–æ–Ω–æ–ø—Ä–æ–µ–∫—Ç–∞—Ö —Ç–∞ —ñ —É –ø–æ—è—Å–Ω—é–≤–∞–ª—å–Ω–∏—Ö –∑–∞–ø–∏—Å–∫–∞—Ö –¥–æ –Ω–∏—Ö –∫–æ–Ω–∫—Ä–µ—Ç–∏–∫–∞ —ñ —Å–ø—Ä–∞–≤–¥—ñ —á–∞—Å—Ç–æ –≤—ñ–¥—Å—É—Ç–Ω—è —è–∫ —Ç–∞–∫–∞, –±–æ, –ø–µ—Ä–µ–≤–∞–∂–Ω–æ –≤–∂–∏–≤–∞—é—Ç—å—Å—è –∞–±—Å—Ç—Ä–∞–∫—Ç–Ω—ñ —Ñ–æ—Ä–º—É–ª—é–≤–∞–Ω–Ω—è, –Ω–∞ –∫—à—Ç–∞–ª—Ç —Ä–µ—Ñ–æ—Ä–º—É–≤–∞–Ω–Ω—è, –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è, —Å—Ç—Ä—É–∫—Ç—É—Ä–∏–∑–∞—Ü—ñ—è, –∑–∞–±–µ–∑–ø–µ—á–µ–Ω–Ω—è, –≤—Ä–µ–≥—É–ª—é–≤–∞–Ω–Ω—è —á–∏ —É–¥–æ—Å–∫–æ–Ω–∞–ª–µ–Ω–Ω—è. –ü—Ä–∏–∫–ª–∞–¥: –£ –¥—ñ—è—Ö ‚Äú—à–∞–ª–µ–Ω–æ–≥–æ –ø—Ä–∏–Ω—Ç–µ—Ä–∞‚Äù –ø–æ–º—ñ—Ç–Ω–∞ —Ç–µ–Ω–¥–µ–Ω—Ü—ñ—è, —è–∫–∞ –ª–µ–¥—å –ø—Ä–æ–≥–ª—è–¥–∞—î—Ç—å—Å—è –∑–∞ –±—ñ–ª—å—à—ñ—Å—Ç—é –∑–∞–ø—Ä–æ–ø–æ–Ω–æ–≤–∞–Ω–∏—Ö –ó–µ–ª–µ–Ω—Å—å–∫–∏–º –∑–º—ñ–Ω ‚Äì –ø–æ..." },
    { title = "–®–∞–Ω.–ü–∞–Ω.–ó–µ", definition = "–®–∞–Ω–æ–≤–Ω–∏–π –ü–∞–Ω–µ –ó–µ–ª–µ–Ω—Å—å–∫–∏–π –ü—Ä–∏–∫–ª–∞–¥: –®–∞–Ω.–ü–∞–Ω.–ó–µ –Ω–µ –≤—ñ–¥–≥—É–∫–Ω—É–≤—Å—è –Ω–∞ –ø—Ä–æ—Ö–∞–Ω–Ω—è –ø—Ä–µ–¥—Å—Ç–∞–≤–Ω–∏–∫—ñ–≤ –ø–∞—Ä–ª–∞–º–µ–Ω—Ç—É –£–∫—Ä–∞—ó–Ω–∏ —É –ü–ê–†–Ñ –ø–æ –ø–∏—Ç–∞–Ω–Ω—é —Å–ø—ñ–≤–ø—Ä–∞—Ü—ñ –ø—Ä–∏ –±–ª–æ–∫—É–≤–∞–Ω–Ω—ñ –ø–æ–≤–µ—Ä–Ω–µ–Ω–Ω—è —Ä–æ—Å—ñ–π—Å—å–∫–æ—ó –¥–µ–ª–µ–≥–∞—Ü—ñ—ó. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –∑–µ–ª—É–ø–∞ –∑–µ–±—ñ–ª –ë–∞—Ä–∏–≥–∞ –ü–æ—Ä–æ—à–∫–æ–≤–∏—á –±–∏–¥–ª–æ–∫—Ä–∞—Ç—ñ—è" },
    { title = "–®–∞–Ω—Å–æ–Ω", definition = "–®–∞–Ω—Å–æ–Ω ‚Äì —É–ª—é–±–ª–µ–Ω–∞ –º—É–∑–∏–∫–∞ –≤–æ–¥—ñ—ó–≤ –º–∞—Ä—à—Ä—É—Ç–æ–∫, –ø—ñ–¥–ª—ñ—Ç–∫—ñ–≤ —ñ –¥–µ—è–∫–∏—Ö —ñ–Ω–¥–∏–≤—ñ–¥—É—É–º—ñ–≤, —è–∫—ñ –ø–æ–≤–µ—Ä–Ω—É–ª–∏—Å—å –∑ –∞—Ä–º—ñ—ó, –≤—ñ—Ç—á–∏–∑–Ω—è–Ω–Ω–∏–π –≤–∞—Ä—ñ–∞–Ω—Ç —à–∞–Ω—Å–æ–Ω—É ‚Äì –º—É–∑–∏–∫–∞ –¥–ª—è —ñ–Ω—Ç–µ–ª–µ–∫—Ç—É–∞–ª—å–Ω–æ—ó –º–µ–Ω—à–æ—Å—Ç—ñ. –ü—Ä–∏–∫–ª–∞–¥: –®–∞–Ω—Å–æ–Ω –ª–∞–±–∞–≤ –Ω–∞ –≤—Å—é –º–∞—Ä—à—Ä—É—Ç–∫—É –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: i—Ç—é–Ω—Å –ú–µ–π–Ω—Å—Ç—Ä—ñ–º —ñ–∑—ñ–ª—ñ—Å–Ω—ñ–Ω–≥ –ì—ñ–≤–Ω–æ" },
    { title = "–©–ê–ë–õ–¨ (–©–ê–ë–ï–õ–¨)", definition = "—Ä—ñ–≤–µ–Ω—å. —Ä–∞–Ω–≥. —Å—Ç–∞—Ç—É—Å. –º–∞—Å—Ç–∞–±–∞. –ø–ª–∞–Ω–∫–∞. –ª–∞–Ω–∫–∞. —Ç–µ—Ä–µ–Ω. –≥—Ä—É–ø–∞. –ü—Ä–∏–∫–ª–∞–¥: –ü–æ–ø—Ä–∞–≤–Ω–∏–π —É—Ä—è–¥ –∑–∞–≤—Å—ñ–¥–∏ –ø—Ä–∞–≥–Ω–µ –∑–≤–∏—â–µ–Ω—Å—Ç–≤–∞ —â–∞–±–ª—é –∂–∏—Ç—Ç—è –ø–æ—Å–ø–æ–ª–∏—Ç–∏—Ö. –î–µ—Ö—Ç–æ –∂–µ–≤—Ä–µ –Ω–∞—á–µ –≤–∞–π–ª–æ –∑ —â–∞–±–ª–µ–º —ñ–Ω—Ç–µ–ª–µ–∫—Ç—É —è–∫ –≤ —Ü–∞–ø–∞. –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: —à–æ–±–ª–∞ –ø–ª–∞–Ω–∫–∞ –∑–∞–ø–∞–ª–∞ –ª–∞–Ω–∫–∞ –ª–µ–≤–µ–ª –ü–∏–∑–¥–æ—Ç–∞" },
    { title = "—â–µ –π –∫—ñ—Ç –Ω–µ –≤–∞–ª—è–≤—Å—è", definition = "—Ç–∞–∫ –∫–∞–∂—É—Ç—å —É —Å–∏—Ç—É–∞—Ü—ñ—ó, –∫–æ–ª–∏ —Ä–æ–±–æ—Ç—É, —è–∫—É –Ω–µ–æ–±—Ö—ñ–¥–Ω–æ –≤–∏–∫–æ–Ω–∞—Ç–∏, —â–µ –π –Ω–µ –ø–æ—á–∏–Ω–∞–ª–∏. —ñ–Ω–æ–¥—ñ –Ω–µ –≤–∞–ª—è–≤—Å—è –Ω–µ –∫—ñ—Ç, –∞ –∫—ñ–Ω—å‚Ä¶ –ü—Ä–∏–∫–ª–∞–¥: –ö–∏—ó–≤–º—ñ—Å—å–∫–±—É–¥ –æ–±—ñ—Ü—è–≤ –ø—ñ–¥–∫–ª—é—á–∏—Ç–∏ –µ–ª–µ–∫—Ç—Ä–∏–∫—É –∑–∞ —Ç–∏–∂–¥–µ–Ω—å, –∞–ª–µ —á–µ—Ä–µ–∑ –º—ñ—Å—è—Ü—å –∑‚Äô—è—Å—É–≤–∞–ª–æ—Å—è —à–æ —Ç–∞–º —â–µ –π –∫—ñ—Ç –Ω–µ –≤–∞–ª—è–≤—Å—è –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –ú—É—Ä–ª–∏–∫–∞ –®–ª–∞–Ω–≥—É–≤–∞—Ç–∏ –§—ñ–ª–æ–Ω–∏—Ç–∏ –ú—É—Ä–∏–∂–∏—Ç–∏—Å—å –ü—Ä–æ–∫—Ä–∞—Å—Ç—É—Ä–±–∞—Ü—ñ—è" },
    { title = "–©—ñ—Ç–ø–æ—Å—Ç", definition = "–í—ñ–¥–µ–æ –≤ –¢—ñ–∫—Ç–æ—Ü—ñ –¥–µ —Ñ–æ–Ω –∑ —á–∏–º–æ—Å—å (–±—É–¥—å-—Ç–æ –≤–ª–∞—Å–Ω–µ –≤—ñ–¥–µ–æ, —á–∏ –≤–∏—Ä—ñ–∑–∫–∞ –∑ —Ñ—ñ–ª—å–º—É/–∞–Ω—ñ–º–µ) –π –Ω–∞ –≤–µ—Å—å –µ–∫—Ä–∞–Ω —Ç–µ–∫—Å—Ç (–í–ª–∞—Å–Ω—ñ –¥—É–º–∫–∏, —Ç–æ—â–æ). –°–∏–Ω–æ–Ω—ñ–º: –õ–µ–¥–∞—á–∏–π –ø–æ—Å—Ç –ü–æ—Ö–æ–¥–∏—Ç—å –∑ –∞–Ω–≥–ª—ñ–π—Å—å–∫–æ—ó. Shitpost- –¥–æ—Å–ª—ñ–≤–Ω–æ: –≥—ñ–º–Ω—è–Ω–∏–π –ø–æ—Å—Ç, —É –Ω–∏—Ö –≤–∏—Å—Ç–∞–≤–ª—è—î—Ç—å—Å—è —â–æ—Å—å –Ω–µ–∑—Ä–æ–∑—É–º—ñ–ª–µ —ñ –∞–±—Å—É—Ä–¥–Ω–µ.–¢–∏–ø—É –º–µ–º–µ –∑ 2016 —Ä–æ–∫—É –ü—Ä–∏–∫–ª–∞–¥: –û—Ç –≤–∏–∫–ª–∞–¥—É –∑–∞—Ä–∞–∑ —â—ñ—Ç–ø–æ—Å—Ç —ñ —Ä–æ–∑–ø–∏—à—É, —è–∫ –Ω–µ–Ω–∞–≤–∏–¥–∂—É —Ä–æ—Å–Ω—é!! –ü–æ–¥—ñ–±–Ω—ñ —Å–ª–æ–≤–∞: –Ω–µ –∑–∞–ø–æ—Å—Ç–∏—à ‚Äî –Ω–µ –∑–∞–ª–∞–π–∫–∞—é—Ç—å —Ç—ñ–∫—Ç–æ–∫–µ—Ä —Ç—Ä–∏–¥–∏–±—ñ–ª—ñ–Ω–≥ –ì–ù–ü –ü–æ—Å—Ç" },
